func TestStorage(t *testing.T) {
	type args struct {
		ctx             context.Context
		client          *rpc.Client
		contractAddress string
		slot            []byte
	}
	tests := []struct {
		name    string
		args    args
		want    []byte
		wantErr error
	}{
		{
			name: "",
			args: args{
				ctx:             context.Background(),
				client:          ETHClient,
				contractAddress: "0x39ca0a6438b6050ea2ac909ba65920c7451305c1",
				slot:            common.Hex2Bytes("0x611ea165ca9257827fc43d2954fdae7d825e82c825d9037db9337fa1bfa93100"),
			},
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := StorageAt(tt.args.ctx, tt.args.client, tt.args.contractAddress, tt.args.slot)
			assert.Nil(t, err)
			assert.Equalf(t, tt.want, got, "Storage(%v, %v, %v, %v)", tt.args.ctx, tt.args.client, tt.args.contractAddress, tt.args.slot)
		})
	}
}

